import './css/controls.css';
import React from 'react';
import leftArrow from './images/left-arrow.png';
import rightArrow from './images/right-arrow.png';
import bottomArrow from './images/arrow-pointing-downwards.png';

// introduce your own eventhandler for eac button Here.
class Controls extends React.Component {
    constructor(){
      super();
      this.wheelRef=React.createRef();
      this.dragging=false;
      this.lastAngle=null;
      this.center={x:0,y:0};
    }

    getAngle(x,y){
      const dx=x-this.center.x;
      const dy=y-this.center.y;
      return Math.atan2(dy,dx)*(180/Math.PI);
    }

    handleMouseDown=(e)=>{
      const rect=this.wheelRef.current.getBoundingClientRect();
      this.center={
        x:rect.left+rect.width/2,
        y:rect.top+rect.height/2,
      }
    }
    render(){
      return (
      //add onclick to the sction
      <section id="controls">
        <div id="wheel">
          <span 
            id="menu-button"
            className="buttons" 
            style={ {top: 20} }
            //Add onClick={}
          >
            Menu
          </span>
          <img className="buttons" draggable="false"
            src={leftArrow} alt="left" 
            style={ {left: 13, width: 40} }>
          </img>
          <img className="buttons" draggable="false"
            src={rightArrow} alt="right" 
            style={ {right: 13, width: 40} }>
          </img>
          <img className="buttons" draggable="false"
            src={bottomArrow} alt="bottom" 
            style={ {bottom: 13, width: 30, height: 37} }>
          </img>
          
          <div id="ok-button" >
            <b>OK</b>
          </div>
        </div>
      </section>
    );
    }
  }
  
  export default Controls;
