import React from "react";
import "./App.css";
import Screen from "./components/Screen";
import Controls from "./components/Controls";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      selectedItemIndex: 0,
      menuItems: ["coverflow", "music", "games", "settings"],
    };
  }
