  // If ok button is clicked,  open the selected component

  // If menu button is clicked, go back to the menu screen

  render() {
    return (
      <>
        <div id="iPod-app">
          <Screen
            selectedItemIndex={this.state.selectedItemIndex}
            menuItems={this.state.menuItems}
          />
          <Controls />
        </div>
      </>
    );
  }
}

export default App;
