import React from "react";
import styles from "./ExpenseForm.module.css";
import { useState } from "react";
export default function ExpenseForm(props){
  // Create state or ref for the inputs here
  const[text,handleText]=useState("");
  const[amount,handleAmount]=use

  handleText = (e) => this.setState({ text: e.target.value });
  handleAmount = (e) => this.setState({ amount: e.target.value });
  handleSubmit = (e) => {
    e.preventDefault();
    const newTransaction = {
      text: this.state.text,
      amount: this.state.amount,
    };
    this.props.addTransaction(newTransaction);
    this.setState({ text: "", amount: "" });
  };


    return (
      <form className={styles.form} onSubmit={this.handleSubmit}>
        <h3>Add new transaction</h3>
        <label htmlFor="expenseText">Text</label>
        <input
          id="expenseText"
          className={styles.input}
          type="text"
          placeholder="Enter text..."
          value={this.state.text}
          onChange={this.handleText}
          required
        />
        <div>
          <label htmlFor="expenseAmount">Amount</label>
          <div>(negative - expense,positive-income)</div>
        </div>
        <input
          className={styles.input}
          id="expenseAmount"
          type="number"
          placeholder="Enter amount..."
          value={this.state.amount}
          onChange={this.handleAmount}
          required
        />
        <button className={styles.submitBtn}>Add Transaction</button>
      </form>
    );
  
}
