import React from "react";
import styles from "./ExpenseForm.module.css";

export default class ExpenseForm extends React.Component {
  // Create state or ref for the inputs here
  constructor() {
    super();
    this.state = {
      text: "",
      amount: "",
    };
  }

  handleText = (e) => this.setState({ text: e.target.value });
  handleAmount = (e) => this.setState({ amount: e.target.value });

  render() {
    const { addTrasaction } = this.props;
    return (
      <form className={styles.form} onSubmit={() => {}}>
        <h3>Add new transaction</h3>
        <label htmlFor="expenseText">Text</label>
        <input
          id="expenseText"
          className={styles.input}
          type="text"
          placeholder="Enter text..."
          required
        />
        <div>
          <label htmlFor="expenseAmount">Amount</label>
          <div>(negative - expense,positive-income)</div>
        </div>
        <input
          className={styles.input}
          id="expenseAmount"
          type="number"
          placeholder="Enter amount..."
          required
        />
        <button className={styles.submitBtn} onClick={t}>Add Transaction</button>
      </form>
    );
  }
}
