import "./App.css";
import React from "react";
import ExpenseForm from "./components/ExpenseForm/ExpenseForm";
import ExpenseInfo from "./components/ExpenseInfo/ExpenseInfo";
import ExpenseList from "./components/ExpenseList/ExpenseList";
export default class App extends React.Component {
  // Create state for the expenses here
  constructor() {
    super();
    this.state = {
      transactions: [],
      totalAmount: "",
      income: "",
      expense: null,
    };
  }

  addTransaction = (newTransaction) => {
    const updatedTransactions = [...this.state.transactions, newTransaction];
    let newTotalAmount=this.state.totalAmount;
    if(newTransaction.amount>0){
      if(newTotalAmount==null){
        newTotalAmount=newTransaction.amount;
      }
      else{
        newTotalAmount+=newTransaction.amount
      }
    }
    else{
      if(newTotalAmount==null){
        newTotalAmount=newTransaction.amount;
      }
      else{
        newTotalAmount-=newTransaction.amount;
      }
    }

    this.setState({ transactions: updatedTransactions, totalAmount:newTotalAmount });
  };

  render() {
    return (
      <>
        <h2 className="mainHeading">Expense Tracker</h2>
        <div className="App">
          {/* Render expense form here */}
          <ExpenseForm addTransaction={this.addTransaction} />
          <div className="expenseContainer">
            {/* Render Expense Info here
            Render Expense List here */}
            <ExpenseInfo
              amount={this.state.totalAmount}
              expense={this.state.expense}
              income={this.state.income}
            />
            <ExpenseList list={this.state.transactions} />
          </div>
        </div>
      </>
    );
  }
}
